import React, { Component } from "react";
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button';
import axios from 'axios';

export default class Edit extends Component {

  constructor(props) {
    super(props)

    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeImg = this.onChangeImg.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    // State
    this.state = {
      title: '',
      description: '',
      img: null,

    }
  }

  componentDidMount() {
    axios.get('http://localhost:8000/api/service/edit/'+this.props.obj.id)
    // axios.get('http://localhost/Vuejs-Larvel-Crud/test/${this.props.obj.id}')
      .then(res => {

        this.setState({
          title: res.data.title,
          description: res.data.description,
          img: res.data.img,

        });
      })

      .catch((error) => {
        console.log(error);
      })
  }

  onChangeTitle(e) {
    this.setState({ title: e.target.value })
  }

  onChangeDescription(e) {
    this.setState({ description: e.target.value })
  }

  onChangeImg(e) {

      this.setState({ img: e.target.files[0],loaded: 0, })

  }



  onSubmit(e) {
    e.preventDefault()

    const productObject = {
      title: this.state.title,
      body: this.state.body,

    };

    axios.put('http://localhost:8000/' + this.props.obj.id, productObject)
      .then((res) => {
        console.log(res.data)
        console.log('Student successfully updated')
      }).catch((error) => {
        console.log(error)
      })

    // Redirect to Student List
    this.props.history.push('/List')
  }


  render() {
    return (
        <section class="" id="">
        <div class="container">
            <div class="text-center">
                <h2 class="section-heading text-uppercase">Add</h2>
            </div>
            <form id="contactForm" name="sentMessage" novalidate="novalidate" onSubmit={this.onSubmit}>
              <div class="form-group">
                  <label for="exampleInputEmail1">Title</label>
                  <input type="text" class="form-control" id=""value={this.state.title} onChange={this.onChangeTitle}  placeholder="Title" />
              </div>
              <div class="form-group">
                  <label for="exampleInputPassword1">Description</label>
                  <textarea class="form-control" id="" placeholder="Description"value={this.state.description} onChange={this.onChangeDescription}></textarea>
              </div>
              <div class="form-group">
                  <label for="exampleFormControlFile1">Image</label>
                  <input type="file"  class="form-control-file" id="exampleFormControlFile1" onChange={this.onChangeImg}/>
              </div>
              <button type="submit" class="btn btn-primary">Submit</button>
            </form>
        </div>
    </section>
    );
  }
}
