      <div class="container">
          <div class="text-center">
              <h2 class="section-heading text-uppercase">Add</h2>
          </div>
          <form id="contactForm" name="sentMessage" novalidate="novalidate" onSubmit={this.handleSubmit}>
            <div class="form-group">
                <label for="exampleInputEmail1">Title</label>
                <input type="text" class="form-control"name="title" id="" onChange={this.handleChange}  placeholder="Title" />
            </div>
            {/* <div class="form-group">
