import React, { Component } from "react";
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button';
import axios from 'axios';

export default class Edit extends Component {

  constructor(props) {
    super(props)

    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);

    // this.onSubmit = this.onSubmit.bind(this);

    // State
    this.state = {

        title: '',
        description: ''

    }
  }



  componentDidMount() {
    // axios.get('http://localhost:8000/api/service/edit/'+this.props.match.params.id)
    // axios.get('http://localhost/laravel-ract/test/'+this.props.obj.id)
    axios.get('http://localhost:8000/api/service/edit/${this.props.match.params.id}')
      .then(res => {

        this.setState({
          title: res.data.title,
          description: res.data.description,

        });
      })

      .catch((error) => {
        console.log(error);
      })
  }

  onChangeTitle(e) {
    this.setState({ title: e.target.value })
  }

  onChangeDescription(e) {
    this.setState({ description: e.target.value })
  }



  // onSubmit(e) {
  //   e.preventDefault()

  //   const productObject = {
  //     title: this.state.title,
  //     description: this.state.description,

  //   };

  //   axios.put('http://localhost/laravel-ract/products/'+this.props.match.params.id, productObject)
  //     .then((res) => {
  //       console.log(res.data)
  //       console.log('Student successfully updated')
  //     }).catch((error) => {
  //       console.log(error)
  //     })

  //   // Redirect to Student List
  //   this.props.history.push('/List')
  // }


  render() {
    // console.log(this.props.match.params.id);
    return (
    <div className="form-wrapper">
      <Form onSubmit={this.onSubmit}>
        <Form.Group controlId="Title">
          <Form.Label>Title</Form.Label>
          <Form.Control type="text" value={this.state.title} onChange={this.onChangeTitle} />
        </Form.Group>

        <Form.Group controlId="Body">
          <Form.Label>Description</Form.Label>
          <Form.Control type="text" value={this.state.description} onChange={this.onChangeDescription} />
        </Form.Group>


        <Button variant="danger" size="lg" block="block" type="submit">
          Update
        </Button>
      </Form>

    </div>
    );
  }
}
