  //   // axios.get('http://localhost/laravel-ract/products/${this.props.match.params.id}/edit')
  //     .then(res => {

  //       this.setState({
  //         title: res.data.title,
  //         description: res.data.description,

  //       });
  //     })

  //     .catch((error) => {
  //       console.log(error);
  //     })
  // }

  onChangeTitle(e) {
    this.setState({ title: e.target.value })
  }

  onChangeDescription(e) {
    this.setState({ description: e.target.value })
  }

  handleId = (e) => {
    console.log(e.target.id);
  }

  onSubmit(e) {
    e.preventDefault()

    const productObject = {
      title: this.state.title,
      description: this.state.description,

    };

    axios.put('http://localhost/laravel-ract/products/'+this.props.match.params.id, productObject)
      .then((res) => {
        console.log(res.data)
        console.log('Student successfully updated')
      }).catch((error) => {
        console.log(error)
      })

    // Redirect to Student List
    this.props.history.push('/List')
  }


  render() {
    console.log(this.props.id);
    return (
    <div className="form-wrapper">
      <Form onSubmit={this.onSubmit}>
        <Form.Group controlId="Title">
          <Form.Label>Title</Form.Label>
          <Form.Control type="text" value={this.state.title} onChange={this.onChangeTitle} />
        </Form.Group>

        <Form.Group controlId="Body">
          <Form.Label>Description</Form.Label>
          <Form.Control type="text" value={this.state.description} onChange={this.onChangeDescription} />
        </Form.Group>

        <button id="yourID" onClick={this.handleId}>Button</button>
        <Button variant="danger" size="lg" block="block" type="submit">
          Update
        </Button>
      </Form>
      {/* <form onSubmit={this.onSubmit}>
                <div className="form-group">
                    <label>Title </label>
                    <input
                      type="text"
                      className="form-control"
                      value={this.state.title  }
                      onChange={this.onChangeTitle}
                      />
                </div>
                <div className="form-group">
                    <label>Body: </label>
                    <input type="text"
                      className="form-control"
                      value={this.state.body  }
                      onChange={this.onChangeBody}
                      />
                </div>

                <div className="form-group">
                    <input type="submit"
                      value="Update Business"
                      className="btn btn-primary"/>
                </div>
            </form> */}
    </div>
    );
  }
}
